final List buffer;
UnboundedReplayBuffer(int capacityHint) {
    this.buffer = new ArrayList(ObjectHelper.verifyPositive(capacityHint, "capacityHint"));
}
ReplaySubject(ReplayBuffer buffer) {
    this.buffer = buffer;
    this.observers = new AtomicReference[]>(EMPTY);
}
然后执行subscribeActual
protected void subscribeActual(Observer observer) {
    ReplayDisposable rs = new ReplayDisposable(observer, this);
    observer.onSubscribe(rs);

    if (!rs.cancelled) {
        if (add(rs)) {
            if (rs.cancelled) {
                remove(rs);
                return;
            }
        }
        buffer.replay(rs);
    }
}
将Observer包装成了ReplayDisposable
ReplayDisposable(Observer actual, ReplaySubject state) {
    this.actual = actual;
    this.state = state;
}
//ReplayDisposable
volatile boolean cancelled;
public void dispose() {
    if (!cancelled) {
        cancelled = true;
        state.remove(this);
    }
}
